import axios from "../helpers.js/axios";
import { authConstants } from "./constants";

export const login = (user) => {

  return async (dispatch) => {

    const res = await axios.post(`/admin/sigin`, {
      ...user
    })

    dispatch({
      type: authConstants.LOGIN_REQUEST,
      payload: {
        ...user
      },
    });
  };
};
