import { categoryConstansts } from "../actions/constants";

const initState = {
    categoryList: [],
    loading: false,
    error: null
}

const buildNewCategories = (categories, category) => {
    let myCategories = []

    for(let cat of categories) {
        myCategories.push({
            ...cat,
            children
        })
    }
    return myCategories
}

export default (state = initState, action) => {

    console.log(action);

    switch (action.type) {
        case categoryConstansts.GET_ALL_CATEGORIES_SUCCESS:
            state = {
                ...state,
                categoryList: action.payload.categoryList
            }
            break;
        case categoryConstansts.ADD_NEW_CATEGORY_REQUEST:
            state = {
                ...state,
                loading: true,
            }
            break;
        case categoryConstansts.ADD_NEW_CATEGORY_SUCCESS:
            const updateCategories = buildNewCategories(state.categoryList, action.payload.category)
            state = {
                ...state,
                loading: false
            }  
            break;
        case categoryConstansts.ADD_NEW_CATEGORY_FAILURE:
            state = {
                initState
            }
            break;
    }
    return state;
}
